{"ops_total_num":{"type":"long","script":{"source":"// 1) Prefer the parsed attribute
def attrs = params._source['Attributes'];
if (attrs != null) {
  def v = attrs['ops_total'];
  if (v != null) {
    try { emit(Long.parseLong(v.toString())); return; } catch (Exception e) {}
  }
}
// 2) Fallback to the raw Body "total: <digits>"
def body = params._source['Body'];
if (body != null) {
  def m = /total:\s*([0-9]+)/.matcher(body.toString());
  if (m.find()) {
    try { emit(Long.parseLong(m.group(1))); } catch (Exception e) {}
  }
}"}}}

